#Explanation
# no of lines are len(string)//k
#Each substring should be of size k


def merge_the_tools(string, k):
    # your code goes here
    n=len(string)//k
    s=list()
    i=0
    while(i<len(string)):
        st=(string[i:i+k])
        i=i+k
        d=dict()
        for m in st:
            d[m]=d.get(m,0)+1
        st=""
        for key,value in d.items():
            st=st+key
        s.append(st)
    print("\n".join(s))

if __name__ == '__main__':
    string, k = input(), int(input())
    merge_the_tools(string, k)
